using System;
using System.Collections.Generic;
using Newtonsoft.Json;

namespace HubSpot.Sync.API.Model.Search
{
    public class SearchResult    {        public int total { get; set; }        public List<Result> results { get; set; }        public Pageing paging { get; set; }    }    public class Result    {        public string id { get; set; }        public long idLong { get { return long.Parse(id); } }        [JsonProperty("properties")]        public Dictionary<string, object> Properties { get; set; }        public DateTime createdAt { get; set; }        public DateTime updatedAt { get; set; }        public bool archived { get; set; }        public Associations associations { get; set; }    }    public class Pageing    {        public Next next { get; set; }    }    public class Associations    {        public AssociationsTypes contacts { get; set; }        public AssociationsTypes deals { get; set; }    }    public class AssociationsTypes    {        public List<AssociationsResult> results { get; set; }    }    public class AssociationsResult    {        public string id { get; set; }        public string type { get; set; }    }    public class Next    {        public string after { get; set; }    }    public class SearchFilters    {        public string after { get; set; }        public int limit { get; set; }        public List<string> properties { get; set; }        public List<Filtergroup> filterGroups { get; set; }    }    public class Filtergroup    {        public List<Filter> filters { get; set; }    }    public class Filter    {        public string propertyName { get; set; }        [JsonProperty("operator")]        public string _operator { get; set; }        public string value { get; set; }    }
}
